INSERT INTO product_table (name, type_id, price, measureType, description) VALUES ('Jogurt siemię lniane śliwka', 3, 2.99, 'szt.', 'Jogurt o smaku śliwki ze zbożami (0,38%) i siemieniem lnianym (0,18%) oraz szczepem bakterii ActiRegularis .');
INSERT INTO product_table (name, type_id, price, measureType, description) VALUES ('Ser kozi plastry z rozmarynem', 3, 9.99, 'szt.', 'Holenderski ser kozi o kremowej konsystencji i łagodnym smaku, który został przełamany dodatkiem aromatycznego rozmarynu.');
INSERT INTO product_table (name, type_id, price, measureType, description) VALUES ('Pasztetowa z borowikiem bezglutenowa', 2, 8.79, 'szt.', 'Pasztetowa z borowikiem przyprawiona naturalnie. ');
INSERT INTO product_table (name, type_id, price, measureType, description) VALUES ('Filet z piersi z kurczaka', 2, 16.99, 'kg.', 'Najwyższej jakości filet klasy A stanowi idealne źródło wartości odżywczych i białka. Dzięki swojej łatwej w przygotowaniu formie znajduje zastosowanie w wielu rodzajach kuchni. Filet to idealna podstawa diety osób aktywnych fizycznie.');
INSERT INTO product_table (name, type_id, price, measureType, description) VALUES ('Stek z antrykotu', 2, 100, 'kg.', 'Stek z antrykotu z selekcjonowanej, polskiej wołowiny dojrzewającej marki TENDER. Pakowany próżoniowo, z młodego bydła ras mięsnych, dojrzewający minimum 14 dni. Stek idealnie nadaje się do smażenia i grillowania. Sugerowany poziom wysmażenia medium-rare. Wyjątkową kruchość i delikatność mięsa gwarantuje krajowy system jakości mięsa wołowego Quality Meat Program (QMP)');
INSERT INTO product_table (name, type_id, price, measureType, description) VALUES ('Szponder wołowy z kością', 2, 30.99, 'kg.', 'Szponder wołowy z kością, bez skóry to dolna część żeberek o ciemnoczerwonej barwie i niewielkim przeroście tłuszczowym. Posiada bogate walory smakowe oraz daje wiele możliwości kulinarnych.');
INSERT INTO product_table (name, type_id, price, measureType, description) VALUES ('Wątróbka z indyka', 2, 12.99, 'kg.', 'Mięso indyka słynie ze swoich walorów zdrowotnych i smakowych. Mięso indycze, w porównaniu z wieprzowiną czy wołowiną, odznacza się lepszymi właściwościami smakowo-zapachowymi i dietetycznymi. Tłuszcz zawarty w mięsie indyka jest lżej strawny i łatwiej przyswajalny. Co bardzo ważne, zawiera więcej nienasyconych kwasów tłuszczowych, niezbędnych w diecie człowieka. Spośród wszystkich gatunków drobiu to właśnie indyk zawiera najwięcej cennych wielonienasyconych kwasów tłuszczowych (PUFA).');
INSERT INTO product_table (name, type_id, price, measureType, description) VALUES ('Chleb chłopski', 1, 5.70, 'szt.', ' Chleb pszenno- żytni wypiekany na naturalnym zakwasie żytnim. Jeden z najczęściej wybieranych chlebów w naszej piekarni, doceniany za swoją uniwersalność i tradycyjny smak. Dzięki dużej ilości kwasu żytniego chleb utrzymuje świeżość na długo. Sprawdzi się idealnie na święta czy dłuższe weekendy  ');
INSERT INTO product_table (name, type_id, price, measureType, description) VALUES ('Chleb żytni z jagodami goji', 1, 10, 'szt.', 'Chleb wypiekany w 100% z mąki żytniej, na naturalnym zakwasie. Wyróżnia go dodatek jagód goji znanych ze swoich prozdrowotnych właściwości. Poprawiają odporność, wpływają korzystnie na pracę serca oraz poziom cukru i cholesterolu we krwi. Chleb zawiera ciemne kiełki żytnie oraz nasiona ostropestu plamistego, który wspomaga trawienie. Dodatek soku jabłkowego nadaje mu przyjemnego aromatu i sprawia, że chleb dłużej zachowuje świeżość.');
INSERT INTO product_table (name, type_id, price, measureType, description) VALUES ('Żytnie Pieczywo lekkie', 1, 4.99, 'szt.', 'Pieczywo lekkie żytnie WASA idealnie nadaje się na pierwsze śniadanie. Dostarczy energii na cały dzień i jednocześnie porcji potrzebnych organizmowi witamin i minerałów. Doskonała alternatywa dla tuczącego, białego pieczywa. Aby wzbogacić jego smak wystarczy dodać ulubione dodatki, np. wędlinę, ser, warzywa czy łososia. Regularne jedzenie tego pieczywa lekkiego jest zalecane w przypadku stosowania diet odchudzających. A gdy dopadnie nas w ciągu dnia mały głód, wówczas także można przegryźć sobie to pieczywo. Wówczas mamy pewność, że nasza figura zawsze będzie zgrabna i powabna.');
INSERT INTO product_table (name, type_id, price, measureType, description) VALUES ('Chałka', 1, 5.99, 'szt.', 'Ta chałka wypiekana jest w oparciu o tradycyjną recepturę znaną od wielu lat, wyłącznie przy wykorzystaniu naturalnych składników. Unikalny, lekko słodki smak zawdzięcza delikatnej posypce z prawdziwej kruszonki. Wykonany z najwyższej klasy drożdżowego ciasta warkocz będzie doskonałym dodatkiem do kubka kakao, przekąską podczas przerwy w pracy, czy szkole, a dodatkowo dobrze sprawdzi się podczas tradycyjnej kolacji wigilijnej. Przy wypieku chałki, piekarnie Putka nie stosują chemicznych polepszaczy smaku ani konsystencji, jednocześnie gwarantując długą trwałość pieczywa.');
INSERT INTO product_table (name, type_id, price, measureType, description) VALUES ('Bułka grahamka', 1, 0.90, 'szt.', 'Bułka pszenno-żytnia z dodatkiem otrąb pszennych, ziaren żyta, płatków owsianych, ziaren słonecznika i siemienia lnianego. Będzie to zdrowa i pożywna baza do zdrowych kanapek. Idealna na śniadanie lub lunch. ');
